import React, { useState } from 'react';
import './Navbar.css';
import Logo from '../Assets/istockphoto-579771988-612x612-removebg-preview.png';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCartShopping } from '@fortawesome/free-solid-svg-icons';
const Navbar = () => {
    const [menu, setMenu] = useState("Home");
        return (
        <>
            <div className='navbar'>
        <div className="nav-logo">
            <img src={Logo} alt="shope logo" />
            <p>Elite Market</p>
        </div>
        <div>
            <ul className="nav-menue">
                <li onClick={()=> {setMenu("Home")}}>Home {menu === "Home" ? <hr /> : <> </>}</li>
                <li onClick={()=> {setMenu("Men")}}>Man {menu === "Men" ? <hr /> : <> </>}</li>
                <li onClick={()=> {setMenu("Women")}}>Women {menu === "Women" ? <hr /> : <> </>}</li>
                <li onClick={()=> {setMenu("Kids")}}>Kids {menu === "Home" ? <hr /> : <> </>}</li>
            </ul>
        </div>

        <div className="nav-login-cart">
            <button>Login</button>
            <FontAwesomeIcon icon={faCartShopping} />
            <div className="nav-cart-count">0</div>
        </div>
        </div>
        <div className="line"></div>
        </>

    )
}

export default Navbar
