import './App.css';
import Navbar from './Components/Navbar/Navbar';
import { Routes, Route } from 'react-router-dom';
import Shop from './Pages/Shop/Shop';
import ShopCategories from './Pages/ShopCategories/ShopCategories';
import Product from './Pages/Products/Product';
import Cart from './Pages/Carts/Cart';
import LoginSignup from './Pages/LoginSignup/LoginSignup';
import Footer from './Components/Footer/Footer';
import man_banner from './Components/Assets/allProducts'

function App() {
  return (
    <div className="App">
      <Navbar />
      <Routes>
        <Route path='/' element={<Shop />}/>
        <Route path='/mans' element={<ShopCategories category = 'man' />}/>
        <Route path='/womens' element={<ShopCategories category = 'women' />}/>
        <Route path='/kids' element={<ShopCategories  category = 'kid'/>}/>
        <Route path='/products' element={Product}>
          <Route path=':productId' element={Product}/>
        </Route>
        <Route path='/cart' element={<Cart />}/>
        <Route path='/login' element={<LoginSignup />}/>
      </Routes>
      <Footer />
    </div>
  );
}

export default App;
